<script>
import emailjs from 'emailjs-com';
export default {
  name: 'App',
  data() {
    return {
      name: '',
      email: '',
      phone: '',
      message: '',
    }
  },
  mounted (){
    let wave1 = document.getElementById('wave1')
    let wave2 = document.getElementById('wave2')
    let wave3 = document.getElementById('wave3')
    let wave4 = document.getElementById('wave4')
      window.addEventListener('scroll', function(){
        let value = window.scrollY
        wave1.style.backgroundPositionX = 400 + value * 1.5 + 'px'
        wave2.style.backgroundPositionX = 300 + value * 1.4 + 'px'
        wave3.style.backgroundPositionX = 200 + value * 1.3 + 'px'
        wave4.style.backgroundPositionX = 100 + value * 1.2 + 'px'
      })

    const toggle = document.getElementById('toggle')
    const navbar = document.getElementById('navbar')
      document.onclick = function(e) {
        if (e.target.id !== 'header' 
          && e.target.id !== 'toggle' 
          && e.target.id !== 'navbar'){
          toggle.classList.remove('active')
          navbar.classList.remove('active')
        }
      }
      toggle.onclick = function() {
        toggle.classList.toggle('active')
        navbar.classList.toggle('active')
      }

    let themeSwitch = document.querySelector('.themeSwitch')
    let body = document.querySelector('body')
      themeSwitch.onclick = function() {
        themeSwitch.classList.toggle('active1')
        body.classList.toggle('dark')
      }


      const faders = document.querySelectorAll('.fade-in');
      const fadeups = document.querySelectorAll('.fade-up');
      const faders1 = document.querySelectorAll('.fade-in1');
      const fadersL = document.querySelectorAll('.fade-left');
      const svgs = document.querySelectorAll('.draw-svg') ;
      const skills = document.querySelectorAll('.draw-skills') ;
      const appearOptions = {
        threshold: 0,
        rootMargin: "0px 0px -300px 0px"
      }
      const appearOnScroll = new IntersectionObserver
      (function(entries, appearOnScroll) {
        entries.forEach(entry => {
          if(!entry.isIntersecting){
            return
            }
          else {
            entry.target.classList.add(`appear`)
            appearOnScroll.unobserve(entry.target)
            }
          })
      }, appearOptions)

      faders.forEach(fader => {
        appearOnScroll.observe(fader)
      })
      faders1.forEach(fader1 => {
        appearOnScroll.observe(fader1)
      })
      fadeups.forEach(fadeup => {
        appearOnScroll.observe(fadeup)
      })
      fadersL.forEach(faderL => {
        appearOnScroll.observe(faderL)
      })
      svgs.forEach(svg => {
        appearOnScroll.observe(svg)
      })
      skills.forEach(skill => {
        appearOnScroll.observe(skill)
      })
      
  },
  methods: {
    sendEmail(e) {
      try {
        if( this.name != '' && this.validEmail && this.validPhone != '' && this.message != ''){
          emailjs.sendForm( "service_49q06wa", "template_3jzpyxi", e.target,"cKSjqbEWib8seuZ8K" , {
            name: this.name,
            email: this.email,
            phone: this.phone,
            message: this.message,
          })
          this.name = ''
          this.email = ''
          this.phone = ''
          this.message = ''
          alert("Send message successfully.")
        }
        else {
          alert("Please enter correct and complete information.")
        }
      } 
      catch(error) {
        console.log({error})
      }
    },
  },
  computed: {
    validEmail() {
      return /^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(this.email)
    },
    validPhone() {
      return /^\(?(\d{3})\)?[- ]?(\d{3})[- ]?(\d{4})$/.test(this.phone)
    },
  }
}
</script>

